    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        boolean respuesta = listaC.eliminar();
        String mensaje = "ERROR! Lista VACIA";
        if (respuesta) {
            mensaje = "SE BORRO CORRECTAMENTE ";
        }
        setTitle(mensaje);
        mostrarLista();
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void btnInsertarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInsertarActionPerformed
        char dato = txtValor.getText().toUpperCase().charAt(0);
        int nivel = sldPrioridad.getValue();
        boolean respuesta = listaC.insertar(dato, nivel);
        String mensaje = "ERROR! no hay memoria RAM, O ya EXISTE el dato en la lista.";
        if (respuesta) {
            mensaje = "EXITO! se insert√≥ correctamente";
        }
        setTitle(mensaje);
        txtValor.setText("");
        mostrarLista();
    }//GEN-LAST:event_btnInsertarActionPerformed

    private void sldPrioridadStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_sldPrioridadStateChanged
        int p=sldPrioridad.getValue(); lblP.setText(p+"");
    }//GEN-LAST:event_sldPrioridadStateChanged
   
    public void mostrarLista() {
        if (listaC.estaVacia()) {
            jtxtResultado.setText("");
            return;
        }
        
        Nodo temp = listaC.ini;
        String cad = "";
        do {
            cad += temp.getDato() + "["+temp.getNivel()+"], ";
            temp = temp.getSig();
        } while (temp != listaC.ini);
        jtxtResultado.setText(cad);
    }
